mapper.select:

			SELECT *
    FROM (
         SELECT rownum rn,review_id,
	    u_id,
	    review_u_id,
		review_content,
		to_char(review_created,'YYYY-MM-DD')
         FROM  ( SELECT * FROM review ORDER BY review_id DESC) 
			            WHERE ROWNUM <= (#{pageNum} * #{amount}
			           )
			)

			WHERE rn > (#{pageNum}-1) * #{amount}

mapper.update:
update review set u_id = #{u_id},
review_content = #{review_content},
review_created = #{review_created}
where bid=#{bid}
